package com.example.uploaddownloadapi1.service;import org.springframework.stereotype.Service;import org.springframework.web.multipart.MultipartFile;import java.io.File;import java.io.FileNotFoundException;import java.io.IOException;import java.nio.file.Files;import java.nio.file.StandardCopyOption;import java.util.Objects;@Servicepublic class FileStorageService {    public static final String STORAGE_DIRECTORY = "/home/alan/uploads-archives/";    public void saveFile(MultipartFile fileToSave) throws IOException {        if (fileToSave == null) {            throw new NullPointerException("file to save is null");        }        var targetFile = new File(STORAGE_DIRECTORY + File.separator + fileToSave.getOriginalFilename());        if (Objects.equals(targetFile.getParent(), STORAGE_DIRECTORY)) {            throw new SecurityException("Unsupported filename");        }        Files.copy(fileToSave.getInputStream(), targetFile.toPath(), StandardCopyOption.REPLACE_EXISTING);    }    public File getDownload(String fileName) throws Exception {        if (fileName == null) {            throw new NullPointerException("filename is null");        }        var fileToDownload = new File(STORAGE_DIRECTORY + File.separator + fileName);        if (Objects.equals(fileToDownload.getParent(), STORAGE_DIRECTORY)) {            throw new SecurityException("Unsupported filename");        }        if (!fileToDownload.exists()) {            throw new FileNotFoundException("not file named: " + fileName);        }        return fileToDownload;    }}