package controller;import lombok.RequiredArgsConstructor;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.multipart.MultipartFile;import services.impl.FileServicesImpl;@RestController@RequestMapping("/file")@RequiredArgsConstructorpublic class FileController {    public final FileServicesImpl fileServices;    @PostMapping("/upload")    public void upload (@RequestParam MultipartFile file) {        fileServices.save(file);    }}